SELECT employee_id, email, job_title
FROM activities
WHERE department = 'Product Development';

SELECT employee_id, email, job_title
FROM activities
WHERE department = 'Finance';

SELECT SUM(num_meetings) 
FROM activities 
WHERE employee_id = 'Na Li';

SELECT employee_id, email, job_title, hours_worked
FROM activities
WHERE week_number = 1 AND hours_worked > 40;

SELECT SUM(total_sales_rmb) 
FROM activities 
WHERE department = 'Sales';

SELECT employee_id, activities
FROM activities
WHERE activities LIKE '%customer retention%' AND activities LIKE '%challenge%' AND activities LIKE '%solution%';

SELECT *
FROM activities
WHERE department = 'IT';

SELECT employee_id, hours_worked
FROM activities
WHERE (employee_id = 'Wei Zhang' OR employee_id = 'Tao Huang') AND week_number = 1;

SELECT SUM(hours_worked) AS total_hours_worked, AVG(total_sales_rmb) AS average_sales_revenue
FROM activities
WHERE department = 'Business Development';
